"use strict";function convertToHours(a){var b=Math.floor(a/60),c=a%60;return b+"h "+c+"m"}var infobarElm=document.querySelector(".infobar time"),specsElm=document.querySelector(".txt-block time"),lengthMinutesInt=parseInt(infobarElm.innerHTML),lengthHours=convertToHours(lengthMinutesInt);infobarElm.innerHTML=lengthHours,specsElm.innerHTML=lengthHours+" ("+lengthMinutesInt+" min)";